import React from 'react';
import { Block } from 'baseui/block';
import { styled } from 'baseui';

export const Container = (props) => {
  return (
    <Block 
      width="80%"
      margin="auto"
    >
      {props.children}
    </Block>
  );
};

export const Centered = styled('div', {
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  height: '100%',
});
