import React, { useContext, useState, useCallback, useEffect } from 'react';
import { Container, MaskedPinInput } from '../../common/StyleClasses';
import { Card } from 'baseui/card';
import { FormControl } from 'baseui/form-control';
import { Input } from 'baseui/input';
import { PinCode } from 'baseui/pin-code';
import AppContext from '../../context/AppContext';
import { useHistory } from 'react-router-dom';
import { Button } from 'baseui/button';
import { userSignup } from '../../backend/api';

const SignupPage = () => {
  const { setError, authDis } = useContext(AppContext);
  const history = useHistory();
  const [username, setUsername] = useState('');
  const [pin, setPin] = useState(["", "", "", ""]);
  const [confirmPin, setConfirmPin] = useState(["", "", "", ""]);
  const [negativePinCode, setNegativePinCode] = useState(false);

  const checkPinsMatch = useCallback(
    () => {
      if (pin[0] === "" || confirmPin[0] === "") {
        return false;
      }

      for (let i in pin) {
        if (parseInt(pin[i]) !== parseInt(confirmPin[i])) {
          return false;
        }
      }
      return pin.length === confirmPin.length;
    },
    [pin, confirmPin],
  );

  useEffect(() => {
    setNegativePinCode(!checkPinsMatch());
  }, [confirmPin])

  return (
    <Container>
      <Card>
        <h2>Sign Up</h2>
        <form onSubmit={(e) => {
          e.preventDefault();

          userSignup(username, parseInt(pin.join()))
          .then()
          .catch()
        }}>
          <FormControl
            label={() => "Username:"}
          >
            <Input
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </FormControl>

          <FormControl
            label={() => "Pin:"}
          >
            <PinCode
              values={pin}
              onChange={({ values }) => setPin(values)}
            />
          </FormControl>

          <FormControl
            label={() => "Confirm Pin:"}
          >
            <PinCode
              values={confirmPin}
              onChange={({ values }) => setConfirmPin(values)}
              error={negativePinCode && confirmPin[0] !== ""}
            />
          </FormControl>

          <Button>Submit</Button>
        </form>
      </Card>
    </Container>
  );
};

export default SignupPage;
