import React, { useEffect, useState, useContext } from 'react';
import { getUserAccounts } from '../../backend/api';
import AppContext from '../../context/AppContext';
import { Container } from '../../common/StyleClasses';
import { Accordion, Panel } from 'baseui/accordion';
import {ListItem, ListItemLabel} from 'baseui/list';
import {Button} from 'baseui/button';

const ListUserAccounts = () => {

  const { auth, app, setError, appDis } = useContext(AppContext);

  const [accounts, setAccounts] = useState([]);

  useEffect(() => {
    getUserAccounts(auth.currentUser)
    .then((response) => {
      console.log(response.accounts);
      setAccounts(response.accounts);
      console.log(accounts);
    })
    .catch(error => {
      setError(error.message);
    });
  }, [])

  return (
    <Container>
      <h2>All Your Accounts at a Glance</h2>
      <Accordion
        onChange={({ expanded }) => console.log(expanded)}
      >
        {accounts.length > 0 && accounts.map((account) => (
          <Panel 
            key={account.number} 
            title={
              <span>Account #: {account.number}</span>
            }
          >
            <ul>
              <ListItem
                endEnhancer={() => `$${account.amount.toFixed(2)}`}
              >
                <ListItemLabel>Balance:</ListItemLabel>
              </ListItem>
              <ListItem
                endEnhancer={() => account.type === 'C' ? 'Checking' : 'Savings'}
              >
                <ListItemLabel>Type:</ListItemLabel>
              </ListItem>
              <ListItem
                endEnhancer={() => account.status === 'A' ? 'Active' : 'Inactive'}
              >
                <ListItemLabel>Status:</ListItemLabel>
              </ListItem>

              <hr/>

              {account.status === "A" && (
                <Accordion>
                  <Panel title="Deposit">deposit form</Panel>
                  <Panel title="Withdraw">withdraw form</Panel>
                  <Panel title="Transfer">transfer form</Panel>
                  <Panel title="Close Account">
                    <h4>Are you sure you want to close this account?</h4>
                    <Button>Yes, Close Account</Button>
                  </Panel>
                </Accordion>
              )}
              {account.status === "I" && (
                <Button>Reopen Account</Button>
              )}
            </ul>

          </Panel>
        ))}
      </Accordion>
    </Container>
  );
};

export default ListUserAccounts;
